import React, { useEffect, useState } from "react";
import { Entypo } from '@expo/vector-icons';
import { DataTable } from "react-native-paper";
import { Button  } from "react-native-paper";
import { View, Text ,StyleSheet } from 'react-native'
import auth from "../../lib/api/auth";

import {
    Menu,
    MenuOptions,
    MenuOption,
    MenuTrigger,
} from 'react-native-popup-menu';
import { MenuProvider } from 'react-native-popup-menu';
import DataRow from "./DataRow";
import EditCases from "./EditCases";
import { useNavigation } from "@react-navigation/native";



export default function DisplayCases(props) {
// const { height, width } = Dimensions.get("window");

    const navigation = useNavigation();
    const [editBool, setEditBool] = useState(false)

    const deleteFunction = async (id) => {
        let data = {
            id: id
        }
        const res = await auth.post("lawyer/deleteCase", data);
        console.log("Deleting response: ", res.status)
    }

    return (
        <MenuProvider>
            <View>
                <Menu style={{ paddingBottom: 80 }}>
                    <MenuTrigger triggerOnLongPress={true} >

                        <DataRow  dat={props.data} index={props.index} />
                    </MenuTrigger>

                    {
                        editBool && (
                            <>
                                <EditCases setEditBool={setEditBool} id={props.data._id} nextHiring={props.data.nextHiring} />
                            </>
                        )
                    }

                    <MenuOptions style={{ marginTop: 0 }}>
                        <MenuOption onSelect={() => setEditBool(!editBool)} text='Edit' />
                        <MenuOption onSelect={() => deleteFunction(props.data._id)} >
                            <Text style={{ color: 'red' }}>Delete</Text>
                        </MenuOption>
                        <MenuOption onSelect={() => navigation.navigate('DisplaySingleCase', { caseData: props.data })}>
                            <Text>View</Text>
                        </MenuOption>
                    </MenuOptions>
                </Menu>
            </View>
        </MenuProvider>
    )
}
