import React, { useState, useEffect } from "react";
import {
    View,
    Text,
    Pressable,
    ScrollView,
    Platform,
    Image,
    StyleSheet,
    Dimensions,
    TouchableOpacity,
} from "react-native";
import {
    fontPixel,
    heightPixel,
    widthPixel,
    pixelSizeHorizontal,
} from "../../lib/style/adjust";
import {
    List,
    Divider,
    Avatar,
    Card,
    Paragraph,
    Button,
} from "react-native-paper";
import server from '../../server/server';
import auth from '../../lib/api/auth';
import { useNavigation } from "@react-navigation/native";
import AsyncStorage from "@react-native-async-storage/async-storage";
const Call = () => {
    const [appointments, setAppointments] = useState([])
    const getUserData = async () => {
        const _id = await AsyncStorage.getItem('userId');
        let id = JSON.parse(_id);
        console.log(id)
        const data = {
            id: id
        }
        const response = await server.get("/");
        console.log(response.data);
        setAppointments(response.data);
    };
    const navigation = useNavigation();

    useEffect(() => {
        getUserData();
    }, []);

    return (
        <ScrollView style={styles.container} showsVerticalScrollIndicator={false}>
            <Text style={styles.heading}>All Appointments</Text>
            <View style={{ marginBottom: 20, marginTop: 20 }}>
                <ScrollView>
                    {appointments.map((data, index) => (
                        <Card style={styles.card}>
                            <Card.Title
                                title={`${data?.userId.firstName}`}
                                subtitle={
                                    <Text style={{ color: "black", fontSize: 14, fontWeight: 'bold' }}>
                                        Fee Charged :<Text style={{ fontWeight: '200' }}> {data?.charges} </Text>
                                    </Text>

                                }
                                left={(props) => (
                                    <TouchableOpacity
                                    >
                                        <Avatar.Image
                                            {...props}
                                            size={50}
                                            source={{ uri: data?.userId.profileImage }}
                                        />
                                    </TouchableOpacity>
                                )}
                            />
                            <View style={{ alignItems: "center", padding: 5 }}>
                                <Pressable
                                    style={{
                                        width: "70%",
                                        borderColor: "#244e78",
                                        borderWidth: 1,
                                        margin: 5,
                                        padding: 5,
                                    }}
                                    // mode="Outlined"
                                    color="#244e78"
                                    onPress={() => { navigation.navigate('Call') }}

                                >
                                    <View style={{
                                        display: 'flex',
                                        flexDirection: 'row',
                                        alignSelf: 'center'
                                    }}>
                                        <Text style={{
                                            textAlign: 'left',
                                            fontWeight: 'bold'
                                        }}>
                                            Date :
                                        </Text>
                                        <Text style={{
                                            textAlign: 'center'
                                        }}> {data?.date}
                                        </Text>
                                    </View>
                                </Pressable>
                                <Pressable
                                    style={{
                                        width: "70%",
                                        borderColor: "#244e78",
                                        borderWidth: 1,
                                        margin: 5,
                                        padding: 5,
                                    }}
                                    // mode="Outlined"
                                    color="#244e78"
                                >
                                    <View style={{
                                        display: 'flex',
                                        flexDirection: 'row',
                                        alignSelf: 'center'
                                    }}>
                                        <Text style={{
                                            textAlign: 'left',
                                            fontWeight: 'bold'
                                        }}>
                                            Status :
                                        </Text>
                                        <Text style={{
                                            textAlign: 'center'
                                        }}> {data?.status}
                                        </Text>
                                    </View>
                                </Pressable>
                            </View>
                        </Card>
                    ))}
                </ScrollView>
            </View>
        </ScrollView>
    );
};
const styles = StyleSheet.create({
    container: {
        flex: 1,
    },
    heading: {
        padding: 10,
        textAlign: "center",
        fontSize: fontPixel(22),
        fontWeight: "bold",
        textTransform: "uppercase",
        marginBottom: 10,
    },
    card: {
        width: "90%",
        padding: 15,
        alignSelf: "center",
        marginBottom: 20,
        borderRadius: 5,
    },
});
export default Call;
