import React, { useState, useEffect } from "react";
import {
    View,
    Text,
    FlatList,
    Image,
    useWindowDimensions,
    Dimensions,
    StyleSheet,
} from "react-native";
import { fontPixel, pixelSizeVertical } from "../../lib/style/adjust";
import { useStyles } from "../../lib/style/useStyle";
import { useSafeAreaInsets } from "react-native-safe-area-context";
// import OrderGoods from '../../components/goods/OrderGoods';
// import StackHeader from '../../components/navigation/StackHeader';
import auth from "../../lib/api/auth";
import { Pressable } from "react-native";
import { ScrollView } from "react-native-gesture-handler";
import RenderHTML from "react-native-render-html";
import { WebView } from "react-native-webview";

const { height, width } = Dimensions.get("window");

const ViewBlawg = ({ route }) => {
    const [blawg, setBlawg] = useState([]);
    const {blawgId} = route.params;
    const getUserData = async () => {
        const data = {
            id: blawgId,
        };
        const response = await auth.post("blawgs/viewABlawg", data);
        console.log(response);
        setBlawg(response.data[0]);
    };

    useEffect(() => {
        getUserData();
    }, []);

    const source = {
        html: blawg?.description,
    };
    const commonStyle = useStyles();
    const insets = useSafeAreaInsets();
    const { width } = useWindowDimensions();
    return (
        <View style={styles.container}>
            <ScrollView showsVerticalScrollIndicator={false}>
                <View>
                    <Text
                        style={{
                            letterSpacing: 1,
                            fontSize: fontPixel(30),
                            fontWeight: "800",
                            textAlign: "center",
                            padding: 10,
                        }}
                    >
                        {blawg.title}
                    </Text>
                    <Image
                        style={{
                            borderRadius: 5,
                            width: "95%",
                            height: "60%",
                            alignSelf: "center",
                        }}
                        source={{ uri: blawg?.imageUrl }}
                    />
                    <View
                        style={{
                            width: "80%",
                            flex: 2,
                            alignSelf: "center",
                            marginBottom: 350,
                        }}
                    >
                        <RenderHTML source={source} />
                    </View>
                </View>
            </ScrollView>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        height: height / 1,
        width: width - 10,
    },
    RenderHTML: {
        width: width - 10,
    },
});

export default ViewBlawg;
