import React, { useState, useEffect } from "react";
import {
    View,
    Text,
    Pressable,
    ScrollView,
    Platform,
    Image,
    StyleSheet,
    Dimensions,
    TouchableOpacity,
} from "react-native";
import {
    fontPixel,
    heightPixel,
    widthPixel,
    pixelSizeHorizontal,
} from "../../lib/style/adjust";
import {
    List,
    Divider,
    Avatar,
    Card,
    Paragraph,
    Button,
} from "react-native-paper";
import server from '../../server/server';
import auth from '../../lib/api/auth';
import { useNavigation } from "@react-navigation/native";
const allLawyers = () => {
    const [lawyerData, setLawyersData] = useState([])
    const getUserData = async () => {
        const response = await auth.post("user/viewLawyers");
        console.log(response.data);
        setLawyersData(response.data);
    };
    const navigation = useNavigation();

    useEffect(() => {
        getUserData();
    }, []);

    return (
        <ScrollView style={styles.container} showsVerticalScrollIndicator={false}>
            <Text style={styles.heading}>Lawyers</Text>
            <View style={{ marginBottom: 20, marginTop: 20 }}>
                <ScrollView>
                    {lawyerData.map((data, index) => (
                        <Card style={styles.card}>
                            <Card.Title
                                title={`${data.firstName}`}
                                subtitle={
                                    <Text style={{ color: "black", fontSize: 14 }}>
                                        Consultation Fees: {data.consultationFee}
                                    </Text>
                                }
                                left={(props) => (
                                    <TouchableOpacity
                                        onPress={() => navigation.navigate("OtherProfile")}
                                    >
                                        <Avatar.Image
                                            {...props}
                                            size={50}
                                            source={{ uri: data.profileImage }}
                                        />
                                    </TouchableOpacity>
                                )}
                            />
                            <View style={{ alignItems: "center", padding: 5 }}>
                                <Button
                                    style={{
                                        width: "70%",
                                        borderColor: "#244e78",
                                        borderWidth: 1,
                                        margin: 5,
                                        padding: 5,
                                    }}
                                    mode="Outlined"
                                    onPress={() => { navigation.navigate('ConsultOnline', { lawyer: data }) }}
                                    color="#244e78"
                                >
                                    Consult online
                                </Button>
                                <Button
                                    style={{
                                        width: "70%",
                                        margin: 5,
                                        padding: 5,
                                    }}
                                    mode="contained"
                                    onPress={() => console.log("Pressed")}
                                    color="#244e78"
                                >
                                    Hire a lawyer
                                </Button>
                            </View>
                        </Card>
                    ))}
                </ScrollView>
            </View>
        </ScrollView>
    );
};
const styles = StyleSheet.create({
    container: {
        flex: 1,
    },
    heading: {
        padding: 10,
        textAlign: "center",
        fontSize: fontPixel(22),
        fontWeight: "bold",
        textTransform: "uppercase",
        marginBottom: 10,
    },
    card: {
        width: "90%",
        padding: 15,
        alignSelf: "center",
        marginBottom: 20,
        borderRadius: 5,
    },
});
export default allLawyers;
