import React, { useEffect, useState } from "react";
import { Entypo } from '@expo/vector-icons';
import { DataTable } from "react-native-paper";
import { Button } from "react-native-paper";
import { View, Text } from 'react-native'
import {
    Menu,
    MenuOptions,
    MenuOption,
    MenuTrigger,
} from 'react-native-popup-menu';
import { MenuProvider } from 'react-native-popup-menu';

export default function DataRow(props) {
    const [toggleMenu, setToggleMenu] = useState(false)

    return (

        <View >
            <DataTable.Row style={{}}>
                <DataTable.Cell style={{ width:70, left: -15, padding: 24, }}>{props.index + 1}</DataTable.Cell>
                <DataTable.Cell style={{ width:120 }}>
                    {props.dat.title && props.dat.title}
                    {!props.dat.title && "No Data Available"}
                </DataTable.Cell>
                <DataTable.Cell style={{  width:180,  left: 15, padding: 24, }}>
                    {props.dat.courtName && props.dat.courtName}
                    {!props.dat.courtName && "No Data Availble"}
                </DataTable.Cell>
                <DataTable.Cell style={{  width:180, left: 15, padding: 24, }}>
                    {props.dat.category && props.dat.category}
                    {!props.dat.category && "No Data is Available"}
                </DataTable.Cell>
                <DataTable.Cell style={{  width:180, left: 15, padding: 24, }}>
                    {props.dat.stage && props.dat.stage}
                    {!props.dat.stage && "No Data Available"}
                </DataTable.Cell>
                <DataTable.Cell style={{  width:180, left: 15, padding: 24, }}>
                    {props.dat.startingDate && props.dat.startingDate}
                    {!props.dat.startingDate && "No data Available"}
                </DataTable.Cell>
                <DataTable.Cell style={{  width:180, left: 15, padding: 24, }}>
                    {props.dat.nextHiring && props.dat.nextHiring}
                    {!props.dat.nextHiring && "No data Available"}
                </DataTable.Cell>
                <DataTable.Cell style={{  width:180, left: 15, padding: 24, }}>
                    {props.dat.notes}
                </DataTable.Cell>
            </DataTable.Row>
        </View>
    )
}
