import React, { useEffect, useState } from "react";
import { Entypo } from '@expo/vector-icons';
import { DataTable } from "react-native-paper";
import { Button } from "react-native-paper";
import {
    View, Text, StyleSheet, Dimensions, ScrollView
} from 'react-native'
import auth from "../../lib/api/auth";
import CalendarStrip from "react-native-calendar-strip";

import {
    Menu,
    MenuOptions,
    MenuOption,
    MenuTrigger,
} from 'react-native-popup-menu';
const { height, width } = Dimensions.get("window");

import { MenuProvider } from 'react-native-popup-menu';
import DataRow from "./DataRow";
import EditCases from "./EditCases";
import DisplayCases from "./DisplayCases";
export default function DisplaySingleCase({ route }) {

    const { caseData } = route.params;
    const [editBool, setEditBool] = useState(false)

    const deleteFunction = async (id) => {
        let data = {
            id: id
        }
        const res = await auth.post("lawyer/deleteCase", data);
        console.log("Deleting response: ", res.status)
    }


    return (

        <View style={styles.table}>



            <ScrollView horizontal>
                <DataTable style={styles.table}>
                    <DataTable.Header>

                        <DataTable.Title style={styles.header}>
                            <Text style={styles.tableHeading}>Case Title</Text>
                        </DataTable.Title>
                        <DataTable.Title style={styles.header}>
                            <Text style={styles.tableHeading}>Court Name </Text>
                        </DataTable.Title>
                        <DataTable.Title style={styles.header}>
                            <Text style={styles.tableHeading}>Category </Text>
                        </DataTable.Title>
                        <DataTable.Title style={styles.header}>
                            <Text style={styles.tableHeading}>Stage </Text>
                        </DataTable.Title>
                        <DataTable.Title style={styles.header}>
                            <Text style={styles.tableHeading}>Start/Prev Date </Text>
                        </DataTable.Title>
                        <DataTable.Title style={styles.header}>
                            <Text style={styles.tableHeading}>Next Date </Text>
                        </DataTable.Title>
                        <DataTable.Title style={styles.header}>
                            <Text style={styles.tableHeading}>Notes</Text>
                        </DataTable.Title>
                    </DataTable.Header>
                    <DataTable.Header>

                        <DataTable.Title style={styles.header}>
                            <Text style={styles.tableHeading}>{caseData.title}</Text>
                        </DataTable.Title>
                        <DataTable.Title style={styles.header}>
                            <Text style={styles.tableHeading}>{caseData.courtName} </Text>
                        </DataTable.Title>
                        <DataTable.Title style={styles.header}>
                            <Text style={styles.tableHeading}>{caseData.category}</Text>
                        </DataTable.Title>
                        <DataTable.Title style={styles.header}>
                            <Text style={styles.tableHeading}>{caseData.stage} </Text>
                        </DataTable.Title>
                        <DataTable.Title style={styles.header}>
                            <Text style={styles.tableHeading}>{caseData.startingDate}</Text>
                        </DataTable.Title>
                        <DataTable.Title style={styles.header}>
                            <Text style={styles.tableHeading}>{caseData.nextHiring}</Text>
                        </DataTable.Title>
                        <DataTable.Title style={styles.header}>
                            <Text style={styles.tableHeading}>{caseData.notes}</Text>
                        </DataTable.Title>
                    </DataTable.Header>
                </DataTable>

            </ScrollView>
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        height: height / 1,
        width: width - 10,
    },
    number: {
        fontSize: 24,
        color: "#244e78",
        alignSelf: "center",
    },
    table: {
        width: "100%",
    },
    tableHeading: {
        fontWeight: "bold",
        color: "black",
    },
    header: {
        padding: 24,
        left: -26,
    },
});
