import React, { useEffect, useState } from "react";
import {
    View,
    Text,
    FlatList,
    Platform,
    TextInput,
    Image,
    StyleSheet,

} from "react-native";
import {
    Button,
} from "react-native-paper";
import auth from "../../lib/api/auth";
import { fontPixel, heightPixel, widthPixel } from "../../lib/style/adjust";
import { useStyles } from "../../lib/style/useStyle";
import { SafeAreaView, useSafeAreaInsets } from "react-native-safe-area-context";
import PixelImage from "../../Components/common/PixelImage";
import FocusAwareStatusBar from "../../Components/common/FocusAwareStatusBar";
import { ScrollView } from "react-native-gesture-handler";
import RenderHTML from "react-native-render-html";
import 'react-native-gesture-handler'
import { useNavigation } from "@react-navigation/native";
const AllBlawgs = () => {
    const [blawgs, setBlawgs] = useState([]);
const navigation = useNavigation();
    const getBlawgs = async () => {
        const response = await auth.post("blawgs/viewBlawgs");
        setBlawgs(response.data);
        console.log(response.data);
    };

    useEffect(() => {
        getBlawgs();
    }, []);


    return (
           
                <View>
                    <Text
                        style={{
                            textAlign: 'center',
                            marginTop: 20,
                            color: "black",
                            fontSize: 24,
                            marginBottom: 20,
                            fontWeight: "bold",
                        }}
                    >
                        Blawgs
                    </Text>
                    <View>
                    <ScrollView >
                    {blawgs.map((data, index) => (
                        <View key={index}
                            style={{
                                height: "30%",
                                display: "flex",
                                flexDirection: "column",
                                alignItems: "center",
                            }}
                        >
                            <Image
                                style={{
                                    borderRadius: 5,
                                    width: "95%",
                                    height: 150,
                                    alignSelf: "center",
                                    left: 5,

                                }}
                                source={{ uri: data?.imageUrl }}
                            />
                            <Text
                                style={{
                                    width: "80%",
                                    alignSelf: "center",
                                    margin: 15,
                                    fontSize: 20,
                                    color: "black",
                                }}
                            >
                                {data?.title}
                            </Text>
                            <View
                                style={{
                                    width: "80%",
                                    flex: 2,
                                    alignSelf: "center",
                                    // marginBottom: 600,
                                }}
                            >
                                <Button
                                    style={{
                                        width: "70%",
                                        margin: 5,
                                        padding: 5,
                                        alignSelf: 'center',
                                    }}
                                    mode="contained"
                                    onPress={() => navigation.navigate('Blawg',{blawgId:data?._id})}
                                    color="#244e78"
                                >
                                    Read More
                                </Button>
                            </View>
                        </View>
                    ))}
            </ScrollView>

                    </View>
                </View>
    );
};
const styles = StyleSheet.create({
    container: {
        height: '100%',
        width: '100%',
    },
});

export default AllBlawgs;
