import React, { useState, useEffect, Component } from "react";
import {
    Text,
    View,
    TextInput,
    Pressable,
    Platform,
    TouchableOpacityBase,
    TouchableOpacity,
    Image
} from "react-native";
import { Button, Divider } from "react-native-paper";

import {
    fontPixel,
    heightPixel,
    pixelSizeHorizontal,
    pixelSizeVertical,
    widthPixel,
    PixelImage,
} from "../../lib/style/adjust";
import { useForm, Controller } from "react-hook-form";
import { useStyles } from "../../lib/style/useStyle";
import { useDispatch, useSelector } from "react-redux";
import { defaultLogin } from "../../Components/store/features/userSlice";
import { LinearGradient } from "expo-linear-gradient";
import AntDesign from "react-native-vector-icons/AntDesign";
import SelectDropdown from "react-native-select-dropdown";
import AsyncStorage from "@react-native-async-storage/async-storage";
import * as ImagePicker from 'expo-image-picker'

AntDesign.loadFont();
import { IconFill, IconOutline } from "@ant-design/icons-react-native";
// import {
//     GoogleSignin,
//     GoogleSigninButton,
//     statusCodes,
// } from 'react-native-google-signin';

import { NavigationContainer } from "@react-navigation/native";
import auth from "../../lib/api/auth";
import { ScrollView } from "react-native-gesture-handler";
const AddBlawgs = () => {
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [city, setCity] = useState("");
    const [areaOfLaw, setAreaOfLaw] = useState("");
    const [category, setCategory] = useState("");
    const [role, setRole] = useState("");
    const countries = ["How TO Articles", "Know Your Rights", "Opinion/News/Analysis"];
    const [userId, setUserId] = useState("");
    const [userInfo, setUserInfo] = useState(null);
    const [gettingLoginStatus, setGettingLoginStatus] = useState(true);
    useEffect(() => {

    }, []);

    const askQuestion = async () => {
        let _id = await AsyncStorage.getItem("userId");
        let id = JSON.parse(_id);

        const data = {
            province: province,
            city: city,
            areaOfLaw: areaOfLaw,
            title: title,
            description: description,
            id: id,
        };
        const response = await auth.post("user/postQuestion", data);
        console.log(response);
    };



    const [error, setError] = React.useState("");
    const [toggleCheckBox, setToggleCheckBox] = React.useState(false);

    const commonStyle = useStyles();
    // const { authStatus } = useSelector(state => state.user);
    let authStatus;
    const addBlawg = async () => {
        const data = {
            title: title,
            category: category,
            description: description,
            imageUrl : image
        };
        const response = await auth.post("blawgs/addBlawg", data);
        alert("Blawgs added successfully")

    }

    const { control, handleSubmit } = useForm({
        defaultValues: {
            province: "",
            city: "",
            areaOfLaw: "",
            title: "",
            description: "",
        },
        mode: "onBlur",
    });
    const onSubmit = (data) => {
        setError("");
        console.log(data);

        askQuestion();
    };
    const onErrors = (err) => {
        console.log(err);
        if (err.email) {
            setError(err.email.message);
            return;
        }
        if (err.password) {
            setError(err.password.message);
            return;
        }
    };

    const displayIcon = () => {
        return (
            <PixelImage
                imageSource={require("../../../assets/dropdown.png")}
                imageWidth={20}
                secondStyle={{
                    marginRight: widthPixel(20),
                }}
            />
        );
    };
    const [image, setImage] = useState(null);
    const pickImage = async () => {


        // No permissions request is necessary for launching the image library
        let result = await ImagePicker.launchImageLibraryAsync({
            mediaTypes: ImagePicker.MediaTypeOptions.All,
            allowsEditing: true,
            aspect: [4, 3],
            quality: 1,
        });

        console.log(result);


        if (!result.cancelled) {
            setImage(result.uri);
        }
    };
    return (
        <View
            style={{ alignItems: "center", padding: 20, backgroundColor: "#FFFFFF" }}
        >
            <ScrollView>

                <Text
                    style={{
                        textAlign: "center",
                        fontSize: 25,
                        fontWeight: "800",
                        color: "#29406C",
                    }}
                >
                    Add B-lawgs
                </Text>
                <Text
                    style={{
                        fontSize: 18,
                        marginTop: 10
                    }}
                >Add Title</Text>
                <Controller
                    control={control}
                    render={({ field: { onChange, onBlur, value } }) => (
                        <TextInput
                            style={commonStyle.input}
                            placeholderTextColor={"#000000"}
                            autoCapitalize="none"
                            autoCompleteType="email"
                            autoCorrect={false}
                            keyboardType="email-address"
                            onBlur={onBlur}
                            onChangeText={setTitle}
                            returnKeyType="next"
                            placeholder="Title"
                            value={title}
                        />
                    )}
                    name="title"
                />
                <View
                    style={{
                        display: "flex",
                        flexDirection: "column",
                        justifyContent: "space-between",
                        width: "100%",
                        alignItems: "center",
                    }}
                >
                    <Text
                        style={{
                            marginTop: 10
                        }}
                    >Select Category</Text>

                    <SelectDropdown
                        data={countries}
                        onSelect={(selectedItem, index) => {
                            setCategory(selectedItem);
                        }}
                        buttonStyle={{
                            margin: 10,
                            borderRadius: 36,
                        }}
                        defaultButtonText={"How TO Articles"}
                        // renderDropdownIcon={displayIcon}
                        // dropdownIconPosition={'right'}
                        buttonTextAfterSelection={(selectedItem, index) => {
                            // text represented after item is selected
                            // if data array is an array of objects then return selectedItem.property to render after item is selected
                            return selectedItem;
                        }}
                        rowTextForSelection={(item, index) => {
                            // text represented for each item in dropdown
                            // if data array is an array of objects then return item.property to represent item in dropdown
                            return item;
                        }}
                    />
                    <Text
                        style={{
                            fontSize: 18,
                            marginTop: 20
                        }}
                    >Add Description</Text>
                    <TextInput
                        style={commonStyle.input}
                        multiline={true}
                        numberOfLines={4}
                        placeholder="Description"
                        onChangeText={setDescription}
                        value={description}
                    />
                    <Button title="Pick an image from camera roll"
                        style={{
                            width: "70%",
                            marginTop: 20,
                            padding: 5,
                            alignSelf: "center",
                            left: 10,
                        }}
                        mode="contained"
                        color="#244e78"
                        onPress={pickImage} >Upload Image</Button>
                    {image && <Image

                        source={{ uri: image }} style={{
                            width: 200, height: 200,
                            marginLeft: 20, marginTop: 20, borderRadius: 10
                        }} />}
                </View>

                <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>

                </View>
                <Pressable
                    style={{
                        marginHorizontal: 20,
                        width: "100%",
                        height: heightPixel(63),
                        borderRadius: heightPixel(31),
                        marginTop: pixelSizeVertical(20),
                        ...Platform.select({
                            ios: {
                                shadowColor: "rgba(0, 0, 0, 0.25)",
                                shadowOffset: {
                                    width: 0,
                                    height: 15,
                                },
                                shadowOpacity: 0.6,
                                shadowRadius: 5,
                            },
                            android: {
                                backgroundColor: "#EEEEEE",
                                elevation: 30,
                            },
                        }),
                    }}
                    onPress={addBlawg}
                >
                    <LinearGradient
                        style={{
                            flex: 1,
                            width: "50%",
                            height: "100%",
                            borderRadius: heightPixel(31),
                            alignItems: "center",
                            justifyContent: "center",
                            alignSelf: "center",
                        }}
                        colors={["#29406C", "#49494B"]}
                    >
                        <Text
                            style={[
                                commonStyle.rubik,
                                {
                                    color: "#FFFFFF",
                                    fontSize: fontPixel(20),
                                    fontWeight: "500",
                                    letterSpacing: 1,
                                },
                            ]}
                        >
                            Submit
                        </Text>
                    </LinearGradient>
                </Pressable>
            </ScrollView>
        </View>
    );
};

export default AddBlawgs;
