import React, { useEffect } from 'react';
import { Text, View, Pressable, Image } from 'react-native';
import { useStyles } from '../../lib/style/useStyle';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import PixelImage from '../../Components/common/PixelImage';
import { pixelSizeVertical, pixelSizeHorizontal, fontPixel, heightPixel, widthPixel } from '../../lib/style/adjust';
import { useNavigation } from '@react-navigation/native';
import AsyncStorage from '@react-native-async-storage/async-storage';
const WelcomeLawyer = () => {



    const commonStyle = useStyles();
    const insets = useSafeAreaInsets();


    const setRole = async () => {
        await AsyncStorage.setItem('userRole', 'Lawyer');
    }

    useEffect(() => {

        setRole()

    });

    const navigation = useNavigation();
    return (
        <View
            style={{
                paddingTop: 0,
                paddingBottom: 0,
                flex: 1,
                backgroundColor: '#FFFFFF',
            }}>
            <View style={{
                justifyContent: 'center',
                alignSelf: 'center',
                top: '10%'
            }}>
                <Text style={{
                    color: '#29406C',
                    fontSize: 25,
                    fontWeight: 'bold'
                }}>
                    Welcome To
                </Text>
            </View>
            <View style={{ alignItems: 'center', marginTop: pixelSizeVertical(100) }}>
                <PixelImage
                    imageSource={require('../../../assets/attornea.png')}
                    imageWidth={250}
                />
            </View>
            <View
                style={{
                    flex: 1,
                    width: '70%',
                    alignItems: 'center',
                    alignSelf: 'center'
                }}>
                <Text
                    style={

                        {
                            color: '#29406C',
                            fontWeight: 'bold',
                            letterSpacing: pixelSizeHorizontal(2),
                            fontSize: fontPixel(30),
                            textAlign: 'center',
                        }
                    }>
                    Digital Arena of Lawyers
                </Text>
                <Pressable
                    style={{
                        flexDirection: 'row',
                        backgroundColor: '#ecc40c',
                        borderColor: 'rgba(242, 242, 242, 0.5)',
                        borderWidth: 2,
                        width: widthPixel(208),
                        height: heightPixel(52),
                        borderRadius: heightPixel(26),
                        alignItems: 'center',
                        justifyContent: 'center',
                        marginTop: heightPixel(50),
                    }}
                    onPress={() => { navigation.navigate('AnswerQueries') }} >
                    <Text
                        style={{
                            color: '#29406C',
                            fontWeight: '800',
                            fontSize: fontPixel(20),
                            textAlign: 'center',
                            letterSpacing: 2
                        }
                        }>
                        START
                    </Text>
                </Pressable>
            </View>


        </View>
    );
};

export default WelcomeLawyer;
