import React from 'react';
import { Text, View, Pressable, Image } from 'react-native';
import { useStyles } from '../../lib/style/useStyle';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import PixelImage from '../../Components/common/PixelImage';
import { pixelSizeVertical, pixelSizeHorizontal, fontPixel, heightPixel, widthPixel } from '../../lib/style/adjust';
import { useNavigation } from '@react-navigation/native';
import { Button } from 'react-native-paper';
const AnswerQueries = () => {
    const commonStyle = useStyles();
    const insets = useSafeAreaInsets();

    const navigation = useNavigation();
    return (
        <View
            style={{
                paddingTop: 0,
                paddingBottom: 0,
                flex: 1,
                backgroundColor: '#FFFFFF',
            }}>

            <View style={{ marginTop: pixelSizeVertical(10) }}>
                <PixelImage
                    imageSource={require('../../../assets/attornea.png')}
                    imageWidth={150}
                />
            </View>
            <View style={{
                alignSelf: 'center'
            }}>
                <PixelImage
                    imageSource={require('../../../assets/icons/answer.png')}
                    imageWidth={250}
                />
            </View>
            <View
                style={{
                    flex: 1,
                    width: '70%',
                    alignItems: 'center',
                    alignSelf: 'center'
                }}>
                <Text
                    style={

                        {
                            color: '#29406C',
                            fontWeight: 'bold',
                            letterSpacing: pixelSizeHorizontal(2),
                            fontSize: fontPixel(28),
                            textAlign: 'center',
                        }
                    }>
                    Answer Queries
                </Text>
                <Text style={

                    {
                        top: '5%',
                        color: '#29406C',
                        fontSize: fontPixel(22),
                        textAlign:'justify'
                    }
                }>Practice your knowledge by answering legal queries. Stand out and enhance your portfolio    </Text>
                <Pressable
                    style={{
                        flexDirection:"row",
                        // alignItems: "center",
                        justifyContent: "center",
                        marginTop: 50,
                        
                    }}
                    onPress={() => { navigation.navigate('TalkToClients') }} >
                     <Button color="#244e78" labelStyle={{ fontSize: 28, marginLeft: 0 }} icon="greater-than" mode="contained" style={{ borderRadius: 36, padding: 15, width: '20%' }} />
                </Pressable>
                
            </View>


        </View>
    );
};

export default AnswerQueries;
