import React, { useState, useEffect } from 'react';
import { View, Text, Platform, Pressable, ScrollView, TouchableOpacity, ActivityIndicator } from 'react-native';
// import PixelImage from '../../components/common/PixelImage';
// import {
//     GoogleSignin,
//     GoogleSigninButton,
//     statusCodes,
// } from 'react-native-google-signin';
import {
    fontPixel,
    heightPixel,
    pixelSizeVertical,
    widthPixel,
} from '../../lib/style/adjust';
// import { Searchbar } from 'react-native-paper';
import { SearchBar } from 'react-native-elements';
import { useStyles } from '../../lib/style/useStyle';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import MenuProfile from '../../Components/home/MenuProfile';
import MenuScore from '../../Components/home/MenuScore';
import MenuMessage from '../../Components/home/MenuMessage';
import MenuTask from '../../Components/home/MenuTask';
import DashboardScore from '../../Components/home/DashboardScore';
import DashboardHealthStatus from '../../Components/home/DashboardHealthStatus';
import FocusAwareStatusBar from '../../Components/common/FocusAwareStatusBar';
import { useDispatch, useSelector } from 'react-redux';
import { DrawerActions } from '@react-navigation/native';
import AsyncStorage from "@react-native-async-storage/async-storage";
import AnswerQuestion from '../../Components/home/AnswerQuestion';
import DiaryBox from '../../Components/home/DiaryBox';
import AppointmentsBox from '../../Components/home/AppointmentsBox';
import ProfileBox from '../../Components/home/ProfileBox';
import BlawgBox from '../../Components/home/BlawgBox';
import AddCitationBox from '../../Components/home/AddCitationBox';
import auth from '../../lib/api/auth';
// AIzaSyCyGKTv08DGfYhGXTZwdANZZWOXyI6ZRRM

const LawyerHome = (navigation) => {

    const [lawyerData, setLawyerData] = useState([]);
    const getUser = async () => {
        let user = await AsyncStorage.getItem('userId');
        const id = JSON.parse(user);
        const data = {
            id: id
        }
        const response = await auth.post('user/getLawyerInfo', data)
        setLawyerData(response.data);
    }

    useEffect(() => {
        getUser();
    }, []);

    // const toggleDrawer=()=>{
    //   props.toggleDrawer;
    // }

    const [searchQuery, setSearchQuery] = React.useState('');
    const onChangeSearch = query => setSearchQuery(query);

    const [isMain, setIsMenu] = React.useState(true);
    const commonStyle = useStyles();
    const insets = useSafeAreaInsets();
    const dispatch = useDispatch();
    return (
        <>
            <View
                style={{
                    flex: 1,
                    backgroundColor: '#FFFFFF',
                    height: '100%'
                }}>

                <ScrollView
                    style={{
                        marginTop: 10,
                        backgroundColor: '#FFFFFF',
                        flex: 1,
                        padding: 20,
                    }}>
                    <View>
                        <Text style={{ alignSelf: 'center', fontSize: 24 }}>Welcome {lawyerData.firstName}{lawyerData.lastName}</Text>
                    </View>
                    <View style={{ flexDirection: 'row', marginTop: 18 }}>
                        <AnswerQuestion />
                        <DiaryBox />
                    </View>
                    <View style={{ flexDirection: 'row', }}>
                        <AppointmentsBox />
                        <ProfileBox />
                    </View>
                    <View style={{ flexDirection: 'row', }}>
                        <BlawgBox />
                        <AddCitationBox />
                    </View>
                    {/* <MenuTask /> */}
                    {/* <DashboardScore /> */}
                    {/* <Shop /> */}
                    {/* <DashboardHealthStatus /> */}
                </ScrollView>
            </View>

        </>

    );
};

export default LawyerHome;
