import React, { useState, useEffect } from "react";
import AsyncStorage from "@react-native-async-storage/async-storage";
import auth from "../../lib/api/auth";
import DataRow from "./DataRow";
import axios from "axios";
import {
  View,
  Text,
  Pressable,
  Image,
  Platform,
  ScrollView,
  TextInput,
  Dimensions,
  StyleSheet,
  TouchableOpacity,
  RefreshControl,
} from "react-native";
import { Button } from "react-native-paper";
import {
  MenuProvider,
  Menu,
  MenuOptions,
  MenuOption,
  MenuTrigger,
} from "react-native-popup-menu";
import { Ionicons } from "@expo/vector-icons";
import { useStyles } from "../../lib/style/useStyle";
import { DataTable } from "react-native-paper";

const { height, width } = Dimensions.get("window");
import { useNavigation } from "@react-navigation/native";
import { useForm, Controller } from "react-hook-form";
import AddNewCase from "./AddNewCase";
import DisplayCases from "./DisplayCases";
import CalendarStrip from "react-native-calendar-strip";

const wait = (timeout) => {
  return new Promise((resolve) => setTimeout(resolve, timeout));
};

const Diary = () => {
  const navigation = useNavigation();
  const [refreshing, setRefreshing] = React.useState(false);

  const onRefresh = React.useCallback(() => {
    setRefreshing(true);
    wait(2000).then(() => setRefreshing(false));
  }, []);

  const commonStyle = useStyles();
  const [name, setName] = useState("today");
  const [todayArray, setTodayArray] = useState([]);
  const [openClosedArray, setOpenClosedArray] = useState([]);
  const [tomorrowArray, setTomorrowArray] = useState([]);
  const [selectedDateData, setselectedDateData] = useState([]);
  const [allCases, setAllCases] = useState([]);
  const [myBool, setBool] = useState(false);

  const getAllCases = async () => {
    let id = await AsyncStorage.getItem("userId");
    const data = {
      id: JSON.parse(id),
    };
    const response = await auth.post("lawyer/getCases", data);
    console.log("All Cases: ", response.data);
    setAllCases(response.data);
  };
  const selectedDay = (val) => {
    const select = new Date(val);
    let date = select.getDate();
    date = date <= 9 ? "0" + date : date;
    let month = select.getMonth() + 1;
    month = month <= 9 ? "0" + month : month;
    let selectedDate = select.getFullYear() + "-" + month + "-" + date;

    console.log("Selected Date: ", selectedDate);

    setName("selectedDate");
    const getData = async () => {
      let id = await AsyncStorage.getItem("userId");
      const data = {
        _id: JSON.parse(id),
        date: selectedDate,
      };
      const response = await auth.post("user/getDateCases", data);
      console.log("Selected Date Data Array: ", response.data.length);
      setselectedDateData([...response.data]);
      console.log("Selected Date status: ", response.status);
    };
    getData();
  };

  const todayCases = (val) => {
    const getData = async () => {
      let _id = await AsyncStorage.getItem("userId");
      console.log("id is: ", _id);
      const data = {
        _id: JSON.parse(_id),
        date: val,
      };
      const response = await auth.post("user/getDateCases", data);
      console.log("Today Cases: ", response.data);
      setTodayArray([...response.data]);
    };
    getData();
  };

  const tomorrowCases = (val) => {
    const getData = async () => {
      let id = await AsyncStorage.getItem("userId");
      const data = {
        _id: JSON.parse(id),
        date: val,
      };
      const response = await auth.post("user/getDateCases", data);
      setTomorrowArray([...response.data]);
    };
    getData();
  };
  const getOpenClosedData = async () => {
    let id = await AsyncStorage.getItem("userId");
    let _id = JSON.parse(id);
    const data = {
      _id: _id,
    };
    const response = await auth.post("user/getOpenClosedCases", data);
    setOpenClosedArray(response.data);
  };
  useEffect(() => {
    let date = new Date().getDate();
    date = date <= 9 ? "0" + date : date;
    let month = new Date().getMonth() + 1;
    month = month <= 9 ? "0" + month : month;
    let todayDate = new Date().getFullYear() + "-" + month + "-" + date;
    todayCases(todayDate);

    //tomorrow's Date
    date = new Date().getDate() + 1;
    date = date <= 9 ? "0" + date : date;
    month = new Date().getMonth() + 1;
    month = month <= 9 ? "0" + month : month;
    let tomorrowDate = new Date().getFullYear() + "-" + month + "-" + date;

    getAllCases();
    tomorrowCases(tomorrowDate);
    getOpenClosedData();
  }, []);

  return (
    <ScrollView
      style={styles.container}
      showsVerticalScrollIndicator={false}
      refreshControl={
        <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
      }
    >
      <View>
        <Text
          style={{
            alignSelf: "center",
            marginTop: 20,
            color: "black",
            fontSize: 24,
            marginBottom: 20,
            fontWeight: "bold",
          }}
        >
          Diary
        </Text>
        <View
          style={{
            backgroundColor: "white",
            height: 300,
            width: "90%",
            alignSelf: "center",
            left: 5,
            alignItems: "center",
          }}
        >
          <View
            style={{
              display: "flex",
              flexDirection: "row",
              margin: 40,
            }}
          >
            <View style={{ alignItems: "center", margin: 10, color: "white" }}>
              <TouchableOpacity>
                <Text style={styles.number}>{allCases.length}</Text>
                <Text onPress={() => setName("totalCases")}>Total Cases</Text>
              </TouchableOpacity>
            </View>
            <View style={{ alignItems: "center", margin: 10 }}>
              <TouchableOpacity>
                <Text style={styles.number}>{todayArray.length}</Text>
                <Text onPress={() => setName("today")}>Cases Today</Text>
              </TouchableOpacity>
            </View>
            <View style={{ alignItems: "center", margin: 10 }}>
              <TouchableOpacity>
                <Text style={styles.number}>{tomorrowArray.length}</Text>
                <Text onPress={() => setName("tomorrow")}>Cases Tomorrow</Text>
              </TouchableOpacity>
            </View>
          </View>
          <View
            style={{
              display: "flex",
              flexDirection: "row",
              margin: 20,
            }}
          >
            <View style={{ alignItems: "center", margin: 10 }}>
              <TouchableOpacity>
                <Text style={styles.number}>{openClosedArray.length}</Text>
                <Text onPress={() => setName("openClosed")}>
                  Open/Closed Cases
                </Text>
              </TouchableOpacity>
            </View>
            <View style={{ alignItems: "center", margin: 10 }}>
              <TouchableOpacity>
                <Ionicons
                  style={{ alignSelf: "center" }}
                  name="settings"
                  size={28}
                  color="#244e78"
                />
                <Text>Settings</Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
        <View style={styles.table}>
          <CalendarStrip
            dateNumberStyle={{ color: "#244e78" }}
            daySelectionAnimation={{
              type: "border",
              duration: 200,
              borderWidth: 1,
              borderHighlightColor: "#244e78",
            }}
            // datesBlacklist={datesBlacklist}
            // datesWhitelist={datesWhitelist}
            dateNameStyle={{ color: "#244e78" }}
            startingDate={new Date()}
            disabledDateNameStyle={{ color: "grey" }}
            disabledDateNumberStyle={{ color: "grey" }}
            scrollable={true}
            onDateSelected={selectedDay}
            style={{ height: 150, paddingTop: 20, paddingBottom: 10 }}
          />

          <ScrollView horizontal>
            <DataTable style={styles.table}>
              <DataTable.Header>
                <DataTable.Title>
                  <Text style={styles.tableHeading}>Sr No.</Text>
                </DataTable.Title>
                <DataTable.Title style={styles.header}>
                  <Text style={styles.tableHeading}>Case Title</Text>
                </DataTable.Title>
                <DataTable.Title style={styles.header}>
                  <Text style={styles.tableHeading}>Court Name </Text>
                </DataTable.Title>
                <DataTable.Title style={styles.header}>
                  <Text style={styles.tableHeading}>Category </Text>
                </DataTable.Title>
                <DataTable.Title style={styles.header}>
                  <Text style={styles.tableHeading}>Stage </Text>
                </DataTable.Title>
                <DataTable.Title style={styles.header}>
                  <Text style={styles.tableHeading}>Start/Prev Date </Text>
                </DataTable.Title>
                <DataTable.Title style={styles.header}>
                  <Text style={styles.tableHeading}>Next Date </Text>
                </DataTable.Title>
                <DataTable.Title style={styles.header}>
                  <Text style={styles.tableHeading}>Notes</Text>
                </DataTable.Title>
              </DataTable.Header>

              {myBool && <AddNewCase />}

              {name == "selectedDate" && (
                <>
                  {selectedDateData.map((data, index) => {
                    return (
                      <View>
                        <DisplayCases
                          style={styles.tableHeading}
                          data={data}
                          index={index}
                        />
                      </View>
                    );
                  })}
                </>
              )}
              {name == "totalCases" && (
                <>
                  {allCases.map((data, index) => {
                    return (
                      <View>
                        <DisplayCases
                          style={styles.tableHeading}
                          data={data}
                          index={index}
                        />
                      </View>
                    );
                  })}
                </>
              )}
              {name == "today" && (
                <>
                  {todayArray.map((data, index) => {
                    return (
                      <View>
                        <DisplayCases
                          style={styles.tableHeading}
                          data={data}
                          index={index}
                        />
                      </View>
                    );
                  })}
                </>
              )}
              {name == "tomorrow" && (
                <>
                  {tomorrowArray.map((data, index) => {
                    return (
                      <View>
                        <DisplayCases
                          style={styles.tableHeading}
                          data={data}
                          index={index}
                        />
                      </View>
                    );
                  })}
                </>
              )}
              {name == "openClosed" && (
                <>
                  {openClosedArray.map((data, index) => {
                    return (
                      <>
                        <View>
                          <MenuProvider>
                            <View>
                              <Menu style={{ paddingBottom: 80 }}>
                                <MenuTrigger triggerOnLongPress={true}>
                                  <DataRow dat={data} index={index} />
                                </MenuTrigger>

                                <MenuOptions style={{ paddingBottom: 40 }}>
                                  <MenuOption
                                    onSelect={() => alert(`Save`)}
                                    text="Save"
                                  />
                                  <MenuOption
                                    onSelect={() => deleteFunction(data._id)}
                                  >
                                    <Text style={{ color: "red" }}>Delete</Text>
                                  </MenuOption>
                                  <MenuOption
                                    onSelect={() => alert(`Not called`)}
                                    disabled={true}
                                    text="Disabled"
                                  />
                                </MenuOptions>
                              </Menu>
                            </View>
                          </MenuProvider>
                        </View>
                      </>
                    );
                  })}
                </>
              )}
            </DataTable>
          </ScrollView>
        </View>
        <Button
          style={{
            width: "70%",
            marginTop: 20,
            padding: 5,
            alignSelf: "center",
            left: 10,
          }}
          mode="contained"
          onPress={() => setBool(!myBool)}
          color="#244e78"
        >
          Add Quick Case
        </Button>
      </View>
    </ScrollView>
  );
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
    height: height / 1,
    width: width - 10,
  },
  number: {
    fontSize: 24,
    color: "#244e78",
    alignSelf: "center",
  },
  table: {
    width: "100%",
  },
  tableHeading: {
    fontWeight: "bold",
    color: "black",
  },
  header: {
    width: 120,
    //
  },
});
export default Diary;
