import React from 'react';
import { StyleSheet, View } from 'react-native';
import PagerView from 'react-native-pager-view';
import GetStarted from '../../Components/onboarding/GetStarted';
import OnBoardingTemplate from '../../Components/onboarding/OnBoardingTemplate';

const ScreenContainer = ({ children }) => (
    <View >{children}</View>
);

const OnBoarding = () => {
    const [activePageID, setActivePageID] = React.useState(0);
    const pagerViewRef = React.useRef(null);

    const onPageSelected = e => {
        const { position } = e.nativeEvent;
        setActivePageID(position);
    };
    const nextPage = () => {
        setActivePageID(activePageID + 1);
        pagerViewRef.current.setPage(activePageID + 1);
    };
    const prePage = () => {
        setActivePageID(activePageID - 1);
        pagerViewRef.current.setPage(activePageID - 1);
    };

    const pagerList = [
        {
            index: 1,
            width: 235,
            imageSource: require('../../../assets/attornea.png'),
            text1: 'Ask a question!',
            text2:
                'Get your legal queries answered from multiple lawyers. Help yourself in making informed and rational decisions',
        },
        {
            index: 2,
            width: 326,
            imageSource: require('../../../assets/attornea.png'),
            text1: 'Talk to a Lawyer',
            text2:
                'Need legal opinion from best lawyers? Attornea connects you with best lawyers in town on a single click. Get your appointment',
        },
        {
            width: 301,
            index: 3,
            imageSource: require('../../../assets/attornea.png'),
            text1: 'Hire a Lawyer',
            text2:
                'Get rid of traditional hassle to hire a lawyer. Signup on Attornea and hire lawyers categorized by speciality and case type on single click',
        },
    ];

    return (
        // <ScreenContainer>
        <PagerView
            ref={pagerViewRef}
            style={styles.pagerView}
            initialPage={0}
            scrollEnabled={false}
            onPageSelected={onPageSelected}
            showPageIndicator="false">
            <View key="0">
                <GetStarted nextPage={nextPage} />
            </View>
            {pagerList.map(item => (
                <View key={item.index}>
                    <OnBoardingTemplate
                        item={item}
                        nextPage={nextPage}
                        prePage={prePage}
                    />
                </View>
            ))}
        </PagerView>
        // </ScreenContainer>
    );
};

const styles = StyleSheet.create({
    pagerView: {
        flex: 1,
    },
});

export default OnBoarding;
