import React from 'react';
import { View, Pressable, Text, Platform } from 'react-native';
import {
    fontPixel,
    heightPixel,
    pixelSizeHorizontal,
    pixelSizeVertical,
    widthPixel,
} from '../../lib/style/adjust';
import Login from '../../Components/auth/Login';
import SignUp from '../../Components/auth/SignUp';
import { LinearGradient } from 'expo-linear-gradient';
import { useStyles } from '../../lib/style/useStyle';
import PixelImage from '../../Components/common/PixelImage';
import { useSafeAreaInsets } from 'react-native-safe-area-context';

const AuthScreen = () => {
    const [isLogin, setIsLogin] = React.useState(true);
    const commonStyle = useStyles();
    const insets = useSafeAreaInsets();
    return (
        <View
            style={{
                paddingTop: 0,
                paddingBottom: 0,
                flex: 1,
                backgroundColor: '#FFFFFF',
            }}>

            <LinearGradient
                style={{
                    flex: 1,
                }}
                colors={['#FFFFFF', '#29406C', '#49494B']}

                start={{ x: 1, y: 0 }}
                end={{ x: 0, y: 1 }}>
                <View
                    style={{ alignItems: 'center', marginTop: pixelSizeVertical(30) }}>
                    <PixelImage
                        imageSource={require('../../../assets/attornea.png')}
                        imageWidth={200}
                    />
                </View>
                <View
                    style={{
                        marginTop: isLogin
                            ? pixelSizeVertical(0)
                            : pixelSizeVertical(0),
                        backgroundColor: '#FFFFFF',
                    }}>
                    <View
                        style={{
                            alignItems: 'center',
                            flexDirection: 'row',
                            justifyContent: 'center',
                            height: heightPixel(50),
                            backgroundColor: '#FFFFFF',
                            ...Platform.select({
                                ios: {
                                    shadowColor: 'rgba(0, 0, 0, 0.05)',
                                    shadowOffset: {
                                        width: 0,
                                        height: 10,
                                    },
                                    shadowOpacity: 0.9,
                                    shadowRadius: 1,
                                },
                                android: {
                                    elevation: 15,
                                },
                            }),
                        }}>
                        <Pressable
                            style={{
                                borderBottomColor: isLogin ? '#A01DE3' : '#FFFFFF',
                                flex: 1,
                                marginHorizontal: 20,
                                height: '100%',
                                justifyContent: 'center',
                                alignItems: 'center',
                                borderBottomWidth: 2,
                            }}
                            onPress={() => setIsLogin(true)}>
                            <Text
                                style={
                                    {
                                        fontWeight: 'normal',
                                        textAlign: 'center',
                                        color: '#222222',
                                        fontSize: fontPixel(20),
                                    }
                                }>
                                Login
                            </Text>
                        </Pressable>

                        <View
                            style={{
                                backgroundColor: '#DDDDDD',
                                width: widthPixel(3),
                                height: heightPixel(22),
                            }}
                        />
                        <Pressable
                            style={{
                                borderBottomColor: !isLogin ? '#A01DE3' : '#FFFFFF',
                                flex: 1,
                                marginHorizontal: 20,
                                height: '100%',
                                justifyContent: 'center',
                                alignItems: 'center',
                                borderBottomWidth: 2,
                            }}
                            onPress={() => setIsLogin(false)}>
                            <Text
                                style={
                                    {
                                        fontWeight: 'normal',
                                        textAlign: 'center',
                                        color: '#222222',
                                        fontSize: fontPixel(20),
                                    }
                                }>
                                Sign Up
                            </Text>
                        </Pressable>

                    </View>
                    {isLogin ? <Login /> : <SignUp />}

                </View>

            </LinearGradient>
        </View>
    );
};

export default AuthScreen;
