import React, { useState, useEffect } from "react";
import {
    View,
    Text,
    Pressable,
    ScrollView,
    Platform,
    Image,
    StyleSheet,
    Dimensions,
    TouchableOpacity,
    Linking
} from "react-native";
import {
    fontPixel,
    heightPixel,
    widthPixel,
    pixelSizeHorizontal,
} from "../../lib/style/adjust";
import {
    List,
    Divider,
    Avatar,
    Card,
    Paragraph,
    Button,
} from "react-native-paper";
import { useNavigation } from "@react-navigation/native";
import whatsapp from '../../../assets/hi.png';
const Payment = ({ navigation, route }) => {
    // const navigation = useNavigation();
    const { data } = route.params;
    useEffect(() => {
    }, []);

    return (
        <View>
            <Text style={styles.heading}>Payment</Text>
            <View style={{ marginBottom: 20, marginTop: 20 }}>
                <Text style={styles.heading}>Bank Transfer</Text>
                <Text style={styles.heading}>Account Number: 12345678910101 </Text>
                <Text style={styles.Subheading}>Lawyer details are given below</Text>

                <Card style={styles.card}>
                    <Card.Title
                        title={`${data.firstName}`}
                        subtitle={
                            <Text style={{ color: "black", fontSize: 14 }}>
                                Consultation Fees: {data.consultationFee}
                            </Text>
                        }
                        left={(props) => (

                            <Avatar.Image
                                {...props}
                                size={50}
                                source={{ uri: data.profileImage }}
                            />

                        )}
                    />
                    <View style={{
                        top: '20%',
                        backgroundColor: '#244e78'
                    }}>
                        <Text style={styles.heading1}
                        >Send Payment receipt to have a live consultation with the lawyer you choose</Text>
                        <Pressable style={{
                            backgroundColor: 'white',
                            borderRadius: 36,
                            width: '50%',
                            alignSelf: 'center'
                        }}
                            onPress={() => {
                                Linking.openURL(
                                    'http://api.whatsapp.com/send?phone=923354655480'
                                );
                            }}>
                            <Text
                                style={styles.heading2}
                            >
                                whatsapp us

                            </Text>
                            <Image
                                style={{ width: 60, height: 60, alignSelf: 'center' }}
                                source={whatsapp}
                            />
                        </Pressable>
                    </View>
                </Card>
            </View>
        </View>
    );
};
const styles = StyleSheet.create({
    container: {
        flex: 1,
    },
    heading: {
        padding: 10,
        textAlign: "center",
        fontSize: fontPixel(22),
        fontWeight: "bold",
        textTransform: "uppercase",
        marginBottom: 10,
    },
    heading1: {
        padding: 10,
        textAlign: "center",
        fontSize: fontPixel(18),
        fontWeight: "bold",
        color: 'white',
        textTransform: "uppercase",
        marginBottom: 10,
    },
    heading2: {
        padding: 10,
        textAlign: "center",
        fontSize: fontPixel(18),
        fontWeight: "bold",
        color: 'black',
        textTransform: "uppercase",
        marginBottom: 10,
    },
    Subheading: {
        padding: 5,
        textAlign: "center",
        fontSize: fontPixel(14),
        fontWeight: "bold",
        textTransform: "uppercase",
        marginBottom: 10,
    },
    card: {
        width: "90%",
        padding: 15,
        alignSelf: "center",
        marginBottom: 20,
        borderRadius: 5,
    },
});
export default Payment;
