import React from "react";
import { Text, View, Pressable, Image } from "react-native";
import { useStyles } from "../../lib/style/useStyle";
import { useSafeAreaInsets } from "react-native-safe-area-context";
import PixelImage from "../../Components/common/PixelImage";
import {
    pixelSizeVertical,
    pixelSizeHorizontal,
    fontPixel,
    heightPixel,
    widthPixel,
} from "../../lib/style/adjust";
import { useNavigation } from "@react-navigation/native";
import { Button } from "react-native-paper";
const AskQuestion = () => {
    const commonStyle = useStyles();
    const insets = useSafeAreaInsets();

    const navigation = useNavigation();
    return (
        <View
            style={{
                paddingTop: 0,
                paddingBottom: 0,
                flex: 1,
                backgroundColor: "#FFFFFF",
            }}
        >
            <View style={{ marginTop: pixelSizeVertical(10) }}>
                <PixelImage
                    imageSource={require("../../../assets/attornea.png")}
                    imageWidth={150}
                />
            </View>
            <View
                style={{
                    alignSelf: "center",
                }}
            >
                <PixelImage
                    imageSource={require("../../../assets/icons/user-1.png")}
                    imageWidth={250}
                />
            </View>
            <View
                style={{
                    flex: 1,
                    width: "70%",
                    alignItems: "center",
                    alignSelf: "center",
                }}
            >
                <Text
                    style={{
                        color: "#29406C",
                        fontWeight: "bold",
                        letterSpacing: pixelSizeHorizontal(2),
                        fontSize: fontPixel(28),
                        // textTransform: 'uppercase',
                        textAlign: "center",
                    }}
                >
                    Ask a question!
                </Text>
                <Text
                    style={{
                        top: "5%",
                        color: "#29406C",
                        fontSize: fontPixel(22),
                    }}
                >
                    Get your legal queries answered from multiple lawyers. Help yourself
                    in making informed and rational decisions{" "}
                </Text>
                <Pressable
                    style={{
                        alignItems: "center",
                        justifyContent: "center",
                        marginTop: heightPixel(50),
                    }}
                    onPress={() => {
                        navigation.navigate("HireLawyer");
                    }}
                >

                    <Button color="#244e78" labelStyle={{ fontSize: 28, marginLeft: 3 }} icon="greater-than" mode="contained" style={{ borderRadius: 18, padding: '8%', width: '50%' }} />
                </Pressable>
            </View>
        </View>
    );
};

export default AskQuestion;
