import React from 'react';
import { Text, View, Pressable, Image } from 'react-native';
import { useStyles } from '../../lib/style/useStyle';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import PixelImage from '../../Components/common/PixelImage';
import { pixelSizeVertical, pixelSizeHorizontal, fontPixel, heightPixel, widthPixel } from '../../lib/style/adjust';
import { LinearGradient } from 'expo-linear-gradient';
import { useNavigation } from '@react-navigation/native';
const ChooseRole = () => {
    const commonStyle = useStyles();
    const insets = useSafeAreaInsets();

    const navigation = useNavigation();
    return (
        <View
            style={{
                paddingTop: 0,
                paddingBottom: 0,
                flex: 1,
                backgroundColor: '#FFFFFF',
            }}>
            <View style={{
                justifyContent: 'center',
                alignSelf: 'center',
                top: '10%'
            }}>
                <Text style={{
                    color: '#29406C',
                    fontSize: 25,
                    fontWeight: 'bold'
                }}>
                    Welcome To
                </Text>
            </View>
            <View style={{ alignItems: 'center', marginTop: pixelSizeVertical(100) }}>
                <PixelImage
                    imageSource={require('../../../assets/attornea.png')}
                    imageWidth={250}
                />
            </View>
            <View
                style={{
                    width: '90%',
                    display: 'flex',
                    flexDirection: 'row',
                    justifyContent: 'space-between',
                    alignSelf: 'center'
                }}>
                <Pressable
                    onPress={() => {
                        navigation.navigate('WelcomeUser');
                    }}>
                    <PixelImage
                        imageSource={require('../../../assets/icons/user-icon.png')}
                        imageWidth={150} />
                </Pressable>
                <Pressable
                    onPress={() => {
                        navigation.navigate('WelcomeLawyer');
                    }}>
                    <PixelImage
                        imageSource={require('../../../assets/icons/lawyer-icon.png')}
                        imageWidth={150} />
                </Pressable>
            </View>
            <View
                style={{
                    display: 'flex',
                    alignItems: 'center',
                    flexDirection: 'row',
                    justifyContent: 'space-between',
                    width: '70%',
                    marginLeft: 60

                }}>
                <Text style={{
                    textAlign: 'center',
                    fontSize: 18,
                    marginLeft: 16
                }}>
                    User
                </Text>

                <Text style={{
                    textAlign: 'center',
                    fontSize: 18,
                    marginRight: 13
                }}>
                    Lawyer
                </Text>
            </View>
            <View style={{
                alignSelf: 'center'
            }}>
                {/* <Pressable
                    style={{
                        flexDirection: 'row',
                        backgroundColor: '#ecc40c',
                        borderColor: 'rgba(242, 242, 242, 0.5)',
                        borderWidth: 2,
                        width: widthPixel(208),
                        height: heightPixel(52),
                        borderRadius: heightPixel(26),
                        alignItems: 'center',
                        justifyContent: 'center',
                        marginTop: heightPixel(50),
                    }}
                    onPress={() => { navigation.navigate('LawyerWelcome') }} >
                    <Text
                        style={{
                            color: '#29406C',
                            fontWeight: '800',
                            fontSize: fontPixel(20),
                            textAlign: 'center',
                            letterSpacing: 2
                        }
                        }>
                        START
                    </Text>
                </Pressable> */}
            </View>
        </View>
    );
};

export default ChooseRole;
