import React from 'react';
import { Text, View, Pressable, Image } from 'react-native';
import { useStyles } from '../../lib/style/useStyle';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import PixelImage from '../../Components/common/PixelImage';
import { pixelSizeVertical, pixelSizeHorizontal, fontPixel, heightPixel, widthPixel } from '../../lib/style/adjust';
import { useNavigation } from '@react-navigation/native';
const HireLawyer = () => {
    const commonStyle = useStyles();
    const insets = useSafeAreaInsets();

    const navigation = useNavigation();
    return (
        <View
            style={{
                paddingTop: 0,
                paddingBottom: 0,
                flex: 1,
                backgroundColor: '#FFFFFF',
            }}>

            <View style={{ marginTop: pixelSizeVertical(10) }}>
                <PixelImage
                    imageSource={require('../../../assets/attornea.png')}
                    imageWidth={150}
                />
            </View>
            <View style={{
                alignSelf: 'center'
            }}>
                <PixelImage
                    imageSource={require('../../../assets/icons/user-4.png')}
                    imageWidth={250}
                />
            </View>
            <View
                style={{
                    display: 'flex',
                    flexDirection: 'row',
                    width: '80%',
                    alignItems: 'center',
                    alignSelf: 'center'
                }}>
                <PixelImage
                    imageSource={require('../../../assets/icons/user4.png')}
                    imageWidth={80}
                />

                <Text
                    style={

                        {
                            color: '#29406C',
                            fontWeight: 'bold',
                            letterSpacing: pixelSizeHorizontal(2),
                            fontSize: fontPixel(25),
                            textAlign: 'center',
                        }
                    }>
                    Hire A Lawyer
                </Text>
            </View>
            <View style={{
                display: 'flex',
                flexDirection: 'row',
                width: '70%',
                alignItems: 'center',
                alignSelf: 'center'
            }}>
                <Text style={

                    {
                        top: '5%',
                        color: '#29406C',
                        fontSize: fontPixel(22),
                    }
                }>Get rid of traditional hassle
                    to hire a lawyer Sign up on
                    Attornea and hire lawyers
                    categorized by specialty
                    and case type on a single
                    click.   </Text>

            </View>
            <View style={{
                alignSelf: 'center'
            }}>
                <Pressable
                    style={{
                        flexDirection: 'row',
                        backgroundColor: '#244e78',
                        borderColor: 'rgba(242, 242, 242, 0.5)',
                        borderWidth: 2,
                        width: widthPixel(208),
                        height: heightPixel(52),
                        borderRadius: heightPixel(26),
                        alignItems: 'center',
                        justifyContent: 'center',
                        marginTop: heightPixel(50),
                    }}
                    onPress={() => { navigation.navigate('Auth') }} >
                    <Text
                        style={{
                            color: '#fff',
                            // fontWeight: '800',
                            fontSize: fontPixel(16),
                            textAlign: 'center',
                            letterSpacing: 2
                        }
                        }>
                        Get Started Now
                    </Text>
                </Pressable>
            </View>


        </View>
    );
};

export default HireLawyer;
