import React, { useState, useEffect } from "react";
import { View, Text, FlatList, Dimensions,  Button } from "react-native";
import { fontPixel, pixelSizeVertical } from "../../lib/style/adjust";
import { useStyles } from "../../lib/style/useStyle";
import { useSafeAreaInsets } from "react-native-safe-area-context";
import FocusAwareStatusBar from "../../Components/common/FocusAwareStatusBar";
import auth from "../../lib/api/auth";
import { Pressable } from "react-native";
import PixelImage from "../../Components/common/PixelImage";
import { pixelSizeHorizontal } from "../../lib/style/adjust";
import { widthPixel, heightPixel } from "../../lib/style/adjust";
import { ScrollView } from "react-native-gesture-handler";
import RenderHTML from "react-native-render-html";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { TextInput } from "react-native-paper";

const { width, height } = Dimensions.get("window");

const AnswerAllQueries = ({ navigation, route }) => {
    const { questionId } = route.params;
    const [questionData, setQuestionData] = useState([]);
    const [answer, setAnswer] = useState('');
    const getUserData = async () => {
        console.log("params:", route.params);
        const data = {
            id: questionId
        }
        const response = await auth.post("user/showQuestionUsingId", data);
        console.log(response.data);
        setQuestionData(response.data[0]);
    };


    const answerQuestion = async (questionId) => {
        console.log("questionId:", questionId)
        const userName = await AsyncStorage.getItem("userId");
        let _id = JSON.parse(userName);
        const data = {
            questionId: questionId,
            Description: answer,
            id: _id
        };
        console.log(data);
        const response = await auth.post("user/addComment", data);
        console.log(response);
    }


    useEffect(() => {
        getUserData();
    }, []);

    const commonStyle = useStyles();
    const insets = useSafeAreaInsets();

    const goodsList = [
        {
            id: 1,
            name: "DNA Test",
            price: 195,
            imageUrl: require("../../assets/images/shop/goods1.png"),
        },
        {
            id: 2,
            name: "DNA Test",
            price: 195,
            imageUrl: require("../../assets/images/shop/goods1.png"),
        },
        {
            id: 3,
            name: "DNA Test",
            price: 195,
            imageUrl: require("../../assets/images/shop/goods1.png"),
        },
    ];
    const goodsList1 = [
        {
            id: 1,
            type: "supplement",
            name: "Vitamin D",
            price: 195,
            imageUrl: require("../../assets/images/shop/goods2.png"),
        },
        {
            id: 2,
            type: "supplement",
            name: "Vitamin D",
            price: 195,
            imageUrl: require("../../assets/images/shop/goods2.png"),
        },
        {
            id: 3,
            type: "supplement",
            name: "Vitamin D",
            price: 195,
            imageUrl: require("../../assets/images/shop/goods2.png"),
        },
    ];
    const goodsList2 = [
        {
            id: 1,
            type: "hormone shot",
            name: "Lorem ipsum",
            price: 195,
            imageUrl: require("../../assets/images/shop/goods3.png"),
        },
        {
            id: 2,
            type: "hormone shot",
            name: "Lorem ipsum",
            price: 195,
            imageUrl: require("../../assets/images/shop/goods3.png"),
        },
        {
            id: 3,
            type: "hormone shot",
            name: "Lorem ipsum",
            price: 195,
            imageUrl: require("../../assets/images/shop/goods3.png"),
        },
    ];

    // const renderItem = ({ item, index }) => <OrderGoods item={item} />;

    return (
        <View
            style={{
                padding: 10,
                backgroundColor: "#FFFFFF",
            }}
        >
            <View style={{ margin: pixelSizeVertical(20) }}>
                <Text
                    style={
                        {
                            color: "#29406C",
                            letterSpacing: 1,
                            fontSize: fontPixel(30),
                            fontWeight: "800",
                            textAlign: "center",
                        }
                    }
                >
                    Question Details
                </Text>
            </View>
            <View
                style={{
                    display: "flex",
                    marginBottom: 120,
                }}
            >
                <ScrollView showsVerticalScrollIndicator={false}>
                    {/* {questionData.map((question, index) => ( */}
                    <View
                        style={{
                            borderColor: "#244e78",
                            borderWidth: 1,
                            margin: 10,
                            padding: 5,
                            borderRadius: 5,
                        }}
                    >
                        <View>
                            <View
                                style={{ flexDirection: "column", width: "50%", margin: 5 }}
                            >
                                <Text
                                    style={
                                        {
                                            fontSize: fontPixel(24),
                                            fontWeight: "bold",
                                            letterSpacing: pixelSizeHorizontal(1),
                                        }
                                    }
                                >
                                    Title:
                                </Text>

                                <Text
                                    style={
                                        {
                                            fontSize: fontPixel(15),
                                            fontWeight: "bold",
                                            letterSpacing: pixelSizeHorizontal(1),
                                            top: "15%",
                                        }
                                    }
                                >
                                    {/* {question.title} */}
                                    <RenderHTML source={{ html: questionData?.title }} />
                                </Text>
                            </View>
                            <View
                                style={{
                                    width: "50%",
                                    flexDirection: "column",
                                    justifyContent: "space-between",
                                    marginBottom: 20,
                                    margin: 5,
                                }}
                            >
                                <Text
                                    style={
                                        {
                                            fontSize: fontPixel(24),
                                            fontWeight: "bold",
                                            letterSpacing: pixelSizeHorizontal(1),
                                        }
                                    }
                                >
                                    Description:
                                </Text>
                                <Text
                                    style={
                                        {
                                            color: "#FFFFFF",
                                            fontSize: fontPixel(14),
                                            fontWeight: "bold",
                                        }
                                    }
                                >
                                    {/* {question.description} */}
                                    <RenderHTML source={{ html: questionData?.description }} />
                                </Text>
                            </View>

                            <TextInput
                                style={{ height: 40 }}
                                placeholder="Type here to answer"
                                onChangeText={(e) => setAnswer(e.target.value)}
                                value={answer}
                            />
                            <Button
                                title="submit answer"
                                color={'#244E78'}
                                onPress={() => { answerQuestion(questionData._id) }}
                            />
                            <View
                                style={{
                                    alignSelf: "flex-end",
                                    backgroundColor: "rgba(255, 255, 255, 0.1)",
                                }}
                            />
                        </View>
                    </View>
                    {/* ))} */}
                </ScrollView>
            </View>
        </View>
    );
};

export default AnswerAllQueries;
