import React from 'react';
import { Text, View, Pressable, Image } from 'react-native';
import { useStyles } from '../../lib/style/useStyle';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import PixelImage from '../../Components/common/PixelImage';
import { pixelSizeVertical, pixelSizeHorizontal, fontPixel, heightPixel, widthPixel } from '../../lib/style/adjust';
import { useNavigation } from '@react-navigation/native';
import { Button } from 'react-native-paper';
const OnBoard = () => {
    const commonStyle = useStyles();
    const insets = useSafeAreaInsets();

    const navigation = useNavigation();
    return (
        <View
            style={{
                paddingTop: 0,
                paddingBottom: 0,
                flex: 1,
                backgroundColor: '#FFFFFF',
            }}>

            <View style={{ marginTop: pixelSizeVertical(10) }}>
                <PixelImage
                    imageSource={require('../../../assets/attornea.png')}
                    imageWidth={150}
                />
            </View>
            <View style={{
                alignSelf: 'center'
            }}>
                <PixelImage
                    imageSource={require('../../../assets/icons/user2.png')}
                    imageWidth={280}
                />
            </View>
            <View
                style={{
                    display: 'flex',
                    flexDirection: 'row',
                    width: '70%',
                    alignItems: 'center',
                    // alignSelf: 'center'
                }}>
                <PixelImage
                    imageSource={require('../../../assets/icons/user-2.png')}
                    imageWidth={100}
                />
                <Text
                    style={

                        {
                            color: '#29406C',
                            fontWeight: 'bold',
                            letterSpacing: pixelSizeHorizontal(2),
                            fontSize: fontPixel(28),
                            // textTransform: 'uppercase',
                            textAlign: 'center',
                        }
                    }>
                    Talk to a Lawyer
                </Text>
            </View>
            <View style={{
                width: '80%',
                alignItems: 'center',
                alignContent: 'center',
                alignSelf: 'center'
            }}>
                <Text style={

                    {
                        top: '5%',
                        color: '#29406C',
                        fontSize: fontPixel(22),
                    }
                }>
                    Need legal opinion from best
                    lawyers ? Attornea connects
                    you with best lawyers in town
                    on a single click. Get your
                    appointment.    </Text>

            </View>
            <View style={{
                width: '80%',
                // justifyContent: 'center',
                display: 'flex',
                flexDirection: 'row',
                marginLeft: 15,
                marginTop: 10
            }}>

                <PixelImage
                    imageSource={require('../../../assets/icons/user3.png')}
                    imageWidth={80}
                />
                <Text style={{
                    fontSize: 22,
                    top: '5%'
                }}>Sign up to make Calls</Text>
            </View>
            <View style={{
                alignSelf: 'center'
            }}>
                <Pressable
                    style={{
                        alignItems: "center",
                        justifyContent: "center",
                        marginTop: -40,
                        // borderRadius:'50%'
                    }}
                    onPress={() => {
                        navigation.navigate("HireLawyer");
                    }}
                >

                    <Button color="#244e78" labelStyle={{ fontSize: 28, marginLeft: 0 }} icon="greater-than" mode="contained" style={{ borderRadius: 36, padding: 5, width: '20%' }} />
                </Pressable>
            </View>


        </View >
    );
};

export default OnBoard;
