import React, { useState } from "react";
import { NavigationContainer } from "@react-navigation/native";
import { createDrawerNavigator, DrawerContent, DrawerContentScrollView } from "@react-navigation/drawer";
import CustomDrawerContent from "./src/Components/CustomDrawerContent";
import { createNativeStackNavigator } from "@react-navigation/native-stack";
import { Provider as PaperProvider, DefaultTheme } from "react-native-paper";
import { useDispatch } from "react-redux";
import { getUserById } from "./src/Components/store/features/userSlice";
import OnBoarding from "./src/Screens/onboarding/OnBoarding";
import GetStarted from "./src/Components/onboarding/GetStarted";
import OnBoardingTemplate from "./src/Components/onboarding/OnBoardingTemplate";
import AuthScreen from "./src/Screens/auth/AuthScreen";
import consultOnline from "./src/Screens/ConsultOnline/consultOnline";
import { Image } from "react-native";
import Call from './src/Screens/Appointment/Call';
const theme = {
    ...DefaultTheme,

    colors: {
        ...DefaultTheme.colors,
        primary: "#79901f",
        accent: "#f1c40f",
    },
};

import { useSelector } from "react-redux";
import BottomTabNavigator from "./src/Components/navigators/BottomTabNavigator";
import ViewBlawg from "./src/Screens/Blawgs/ViewBlawg";
import askQuestion from "./src/Screens/askQuestion/askQuestion";
import Profile from "./src/Screens/profile/Profile";
import EditProfile from "./src/Screens/profile/EditProfile";
import AllBlawgs from "./src/Screens/Blawgs/AllBlawgs";
import showAllQuestions from "./src/Screens/AllQuestions/showAllQuestions";
import AllLawyers from "./src/Screens/Lawyers/allLawyers";
import AsyncStorage from "@react-native-async-storage/async-storage";
import showMyQuestions from "./src/Screens/AllQuestions/showMyQuestions";
import AllPosts from "./src/Screens/Posts/AllPosts";
import ChooseRole from "./src/Screens/ChooseRole/ChooseRole";
import WelcomeLawyer from "./src/Screens/LawyersWelcome/WelcomeLawyer";
import AnswerQueries from "./src/Screens/LawyersWelcome/AnswerQueries";
import TalkToClients from "./src/Screens/LawyersWelcome/TalkToClients";
import CustomizeDiary from "./src/Screens/LawyersWelcome/CustomizeDiary";
import GetHired from "./src/Screens/LawyersWelcome/GetHired";
import UserWelcome from "./src/Screens/UserWelcome/UserWelcome";
import OnBoard from "./src/Screens/UserWelcome/OnBoarding";
import Dashboard from "./src/Screens/DashBoard/DashBoard";
import AskQuestion from "./src/Screens/UserWelcome/AskQuestion";
import HireLawyer from "./src/Screens/UserWelcome/HireLawyer";
import LawyerHome from "./src/Screens/home/LawyerHome";
import AnswerAllQueries from "./src/Screens/AnswerQuestions/AnswerQuestions";
// import AddCitation from "./src/Screens/Citation/AddCitation";
import Diary from "./src/Screens/Dairy/Diary";
import AddBlawgs from "./src/Screens/Blawgs/AddBlawgs";
import ViewCitation from "./src/Screens/Citation/ViewCitation";
import AddCitation from "./src/Screens/Citation/AddCitation";
import Payment from "./src/Screens/Payment/Payment";
import Home from "./src/Screens/home/Home";
import DisplayCases from "./src/Screens/Dairy/DisplayCases";
import DisplaySingleCase from "./src/Screens/Dairy/DisplaySingleCase";
import Appointments from "./src/Screens/Appointment/Appointments";
import { HeaderTitle } from "react-navigation-stack";

const Stack = createNativeStackNavigator();
const Drawer = createDrawerNavigator();

function LogoTitle() {
    return (
      <Image
        style={{ width: 60, height: 60 }}
        source={require('./assets/attornea.png')}
      />
    );
  }


  function SideBar() {
    return (
      <Image
        style={{ width: 30, height: 30 }}
        source={require('./assets/bars-solid.png')}
      />
    );
  }

const getUser = async () => {
    const id = await AsyncStorage.getItem("userRole");
    let _id = JSON.parse(id);
};



export const AuthNavigator = () => {
    return (
        <Stack.Navigator
            screenOptions={{
                headerShown: false,
            }}
            initialRouteName="WelcomeLawyer"
        >
            <Stack.Screen name="WelcomeLawyer" component={WelcomeLawyer} />
            <Stack.Screen name="AnswerQueries" component={AnswerQueries} />
            <Stack.Screen name="TalkToClients" component={TalkToClients} />
            <Stack.Screen name="GetHired" component={GetHired} />
            <Stack.Screen name="CustomizeDiary" component={CustomizeDiary} />
            <Stack.Screen name="Auth" component={AuthScreen} />
             <Stack.Screen name="LawyerHome" component={LawNavigator} />

        </Stack.Navigator>
    );
};

export const LawNavigator = () => {
    return (
        <Stack.Navigator
            initialRouteName="HomeScreen"
            screenOptions={{
                headerShown: true,
                headerRight: () => <LogoTitle  />,
                headerLeft:()=><SideBar/>,
                headerTitleAlign:'center'
            }}
        >
            <Stack.Screen name="HomeScreen" component={DrawerNavigator} />
            <Stack.Screen name="AnswerAllQuestions" component={AnswerAllQueries} />
            <Stack.Screen name="Call" component={Call} />
            <Stack.Screen name="CustomizeDiary" component={CustomizeDiary} />
            <Stack.Screen name="Citation" component={ViewCitation} />
            <Stack.Screen name="Profile" component={Profile} />
            <Stack.Screen name="Auth" component={AuthNavigator} />
            <Stack.Screen name="Show Posts" component={AllPosts} />
            <Stack.Screen name="Diary" component={Diary} />
            <Stack.Screen name="AddBlawgs" component={AddBlawgs} />
            <Stack.Screen name="AddCitation" component={AddCitation} />

        </Stack.Navigator>
    );
};




export const ProfileStackNavigation = () => {
    return (
        <Stack.Navigator
            initialRouteName="Auth"
            screenOptions={{
                headerShown: false,
            }}
        >
            <Stack.Screen name="Auth" component={AuthNavigator} />
            <Stack.Screen name="Profile" component={Profile} />
            <Stack.Screen name="LawyerHome" component={LawNavigator} />
        </Stack.Navigator>
    );
};



export const QuestionStack = () => {

    return (
        <Stack.Navigator
            screenOptions={{
                headerShown: false
            }}>
            <Stack.Screen name="ShowAllQuestions" component={showAllQuestions} />
            <Stack.Screen name="AnswerQuestion" component={AnswerAllQueries} />
        </Stack.Navigator>
    )
}


export const BlawgStack = () => {

    return (
        <Stack.Navigator
            screenOptions={{
                headerShown: false
            }}>
            <Stack.Screen name="AllBlawgs" component={AllBlawgs} />
            <Stack.Screen name="Blawg" component={ViewBlawg} />
        </Stack.Navigator>
    )
}



export const DrawerNavigator = () => {
    return (
        <Drawer.Navigator screenOptions={{
            headerShown: false,
        }}
            initialRouteName="Home"
            component={BottomTabNavigator}
        >
            <Drawer.Screen name="Home" component={BottomTabNavigator} />
            <Drawer.Screen name="AllBlawgs" component={BlawgStack} />
            <Drawer.Screen name="EditProfile" component={EditProfile} />

            <Drawer.Screen name="Appointments" component={Appointments} />
            <Drawer.Screen name="AnswerQuestions" component={QuestionStack} />
            <Drawer.Screen name="Diary" component={Diary} />
            <Drawer.Screen name="DisplaySingleCase" component={DisplaySingleCase} />
            {/* <Drawer.Screen name="Show Posts" component={AllPosts} /> */}
            <Drawer.Screen name="Citation" component={ViewCitation} />
            <Drawer.Screen name="Call" component={Call} />

            <Drawer.Screen name="AddBlawgs" component={AddBlawgs} />

        </Drawer.Navigator>
    );
};

export default function Routes() {
    const dispatch = useDispatch();
    const [userToken, setUserToken] = React.useState(null);
    const [userRole, setUserRole] = useState(null);

    const onReady = async () => {
        try {
            const user_id = await AsyncStorage.getItem("userId");
            setUserToken(JSON.parse(user_id));
            const role = await AsyncStorage.getItem("userRole");
        } catch (e) {
            console.error("Error:", e);
        }
    };

    return (
        <NavigationContainer onReady={onReady}>
            {userToken === null ? <AuthNavigator /> : <DrawerNavigator />}
        </NavigationContainer>
    );
}
